package com.atguigu.ssm.beans;

import java.util.List;

public class Department {
	private Integer id;
	private String name;
	private List<Customer> custs;
	
	public Department(Integer id, String name, List<Customer> custs) {
		super();
		this.id = id;
		this.name = name;
		this.custs = custs;
	}
	public Department() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Integer getId() {
		return id;
	}
	public void setId(Integer id) {
		this.id = id;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public List<Customer> getCusts() {
		return custs;
	}
	public void setCusts(List<Customer> custs) {
		this.custs = custs;
	}
	@Override
	public String toString() {
		return "Department [id=" + id + ", name=" + name + ", custs=" + custs + "]";
	}
	
}
